/*==================================================
src/App.js

This is the top-level component of the app.
It contains the top-level state.
==================================================*/
import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';

// Import other components
import Home from './components/Home';
import UserProfile from './components/UserProfile';
import LogIn from './components/Login';
import Credits from './components/Credits';
import Debits from './components/Debits';

class App extends Component {
  constructor() {  // Create and initialize state
    super();
    this.state = {
      accountBalance: 1234567.89, // Initial balance; change to 0 if preferred
      creditList: [],             // Start with empty credits list
      debitList: [],              // Start with empty debits list
      currentUser: {
        userName: 'Joe Smith',
        memberSince: '11/22/99',
      }
    };
  }

  // Update state's currentUser (userName) after "Log In" button is clicked
  mockLogIn = (logInInfo) => {
    const newUser = { ...this.state.currentUser };
    newUser.userName = logInInfo.userName;
    this.setState({ currentUser: newUser });
  }

  // Add a new credit and update account balance accordingly
  addCredit = (credit) => {
    const newCreditList = [...this.state.creditList, credit];
    this.setState({
      creditList: newCreditList,
      accountBalance: this.calculateAccountBalance(newCreditList, this.state.debitList),
    });
  }

  // Add a new debit and update account balance accordingly
  addDebit = (debit) => {
    const newDebitList = [...this.state.debitList, debit];
    this.setState({
      debitList: newDebitList,
      accountBalance: this.calculateAccountBalance(this.state.creditList, newDebitList),
    });
  };

  // Calculate account balance = sum(credits) - sum(debits)
  calculateAccountBalance = (creditList = [], debitList = []) => {
    const totalCredits = creditList.reduce((sum, credit) => sum + credit.amount, 0);
    const totalDebits = debitList.reduce((sum, debit) => sum + debit.amount, 0);
    return (totalCredits - totalDebits).toFixed(2);
  }

  // Create Routes and React elements to be rendered using React components
  render() {
    // Create React elements and pass input props to components
    const HomeComponent = () => (<Home accountBalance={this.state.accountBalance} />);
    const UserProfileComponent = () => (
      <UserProfile
        userName={this.state.currentUser.userName}
        memberSince={this.state.currentUser.memberSince}
      />
    );
    const LogInComponent = () => (
      <LogIn user={this.state.currentUser} mockLogIn={this.mockLogIn} />
    );

    // Pass addCredit method to Credits component
    const CreditsComponent = () => (
      <Credits
        credits={this.state.creditList}
        addCredit={this.addCredit}
      />
    );

    // Pass addDebit method to Debits component
    const DebitsComponent = () => (
      <Debits
        debits={this.state.debitList}
        addDebit={this.addDebit}
      />
    );

    // Important: Include the "basename" in Router if deploying to GitHub Pages or change accordingly
    return (
      <Router basename="/bank-of-react-starter-code">
        <div>
          <Route exact path="/" render={HomeComponent} />
          <Route exact path="/userProfile" render={UserProfileComponent} />
          <Route exact path="/login" render={LogInComponent} />
          <Route exact path="/credits" render={CreditsComponent} />
          <Route exact path="/debits" render={DebitsComponent} />
        </div>
      </Router>
    );
  }
}

export default App;
