import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import axios from 'axios';

import Home from './components/Home';
import UserProfile from './components/UserProfile';
import LogIn from './components/Login';
import Credits from './components/Credits';
import Debits from './components/Debits';

class App extends Component {
  constructor() {
    super();
    this.state = {
      accountBalance: 0,
      creditList: [],
      debitList: [],
      currentUser: {
        userName: 'Joe Smith',
        memberSince: '11/22/99',
      }
    };
  }

  // Fetch credits and debits from API and calculate account balance
  async componentDidMount() {
    const creditURL = "https://johnnylaicode.github.io/api/credits.json";
    const debitURL = "https://johnnylaicode.github.io/api/debits.json";

    const [creditsRes, debitsRes] = await Promise.all([
      axios.get(creditURL),
      axios.get(debitURL)
    ]);

    const credits = creditsRes.data;
    const debits = debitsRes.data;

    const totalCredits = credits.reduce((sum, item) => sum + item.amount, 0);
    const totalDebits = debits.reduce((sum, item) => sum + item.amount, 0);
    const accountBalance = (totalCredits - totalDebits).toFixed(2);

    this.setState({
      creditList: credits,
      debitList: debits,
      accountBalance: parseFloat(accountBalance)
    });
  }

  // Add new credit
  addCredit = (event) => {
    event.preventDefault();
    const description = event.target.description.value;
    const amount = parseFloat(event.target.amount.value);

    const newCredit = {
      id: Date.now(),
      description,
      amount,
      date: new Date().toISOString()
    };

    const updatedCredits = [...this.state.creditList, newCredit];
    const updatedBalance = (this.state.accountBalance + amount).toFixed(2);

    this.setState({
      creditList: updatedCredits,
      accountBalance: parseFloat(updatedBalance)
    });
  }

  // Add new debit
  addDebit = (event) => {
    event.preventDefault();
    const description = event.target.description.value;
    const amount = parseFloat(event.target.amount.value);

    const newDebit = {
      id: Date.now(),
      description,
      amount,
      date: new Date().toISOString()
    };

    const updatedDebits = [...this.state.debitList, newDebit];
    const updatedBalance = (this.state.accountBalance - amount).toFixed(2);

    this.setState({
      debitList: updatedDebits,
      accountBalance: parseFloat(updatedBalance)
    });
  }

  mockLogIn = (logInInfo) => {
    const newUser = { ...this.state.currentUser };
    newUser.userName = logInInfo.userName;
    this.setState({ currentUser: newUser });
  }

  render() {
    const HomeComponent = () => <Home accountBalance={this.state.accountBalance} />
    const UserProfileComponent = () => (
      <UserProfile userName={this.state.currentUser.userName} memberSince={this.state.currentUser.memberSince} />
    );
    const LogInComponent = () => <LogIn user={this.state.currentUser} mockLogIn={this.mockLogIn} />
    const CreditsComponent = () => (
      <Credits
        credits={this.state.creditList}
        addCredit={this.addCredit}
        accountBalance={this.state.accountBalance}
      />
    );
    const DebitsComponent = () => (
      <Debits
        debits={this.state.debitList}
        addDebit={this.addDebit}
        accountBalance={this.state.accountBalance}
      />
    );

    return (
      <Router basename="/bank-of-react-starter-code">
        <div>
          <Route exact path="/" render={HomeComponent} />
          <Route exact path="/userProfile" render={UserProfileComponent} />
          <Route exact path="/login" render={LogInComponent} />
          <Route exact path="/credits" render={CreditsComponent} />
          <Route exact path="/debits" render={DebitsComponent} />
        </div>
      </Router>
    );
  }
}

export default App;
