/*==================================================
src/components/Debits.js

The Debits component contains information for Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/

import React from 'react';
import { Link } from 'react-router-dom';

const Debits = (props) => {
  // Handle form submission when a user adds a new debit
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent page reload

    // Extract values from the form
    const description = e.target.description.value;
    const amount = parseFloat(e.target.amount.value); // Convert input to float
    const date = new Date().toISOString(); // Get current date/time in ISO format

    // Call the addDebit function passed from App.js to update the state
    props.addDebit({ description, amount, date });

    // Clear the form after submission
    e.target.reset();
  };

  return (
    <div>
      <h1>Debits</h1>

      {/* Display the list of debits */}
      <ul>
        {props.debits.map((debit, index) => (
          <li key={index}>
            {/* Show debit description, amount (formatted to 2 decimals), and date (YYYY-MM-DD) */}
            {debit.description} - ${debit.amount.toFixed(2)} on {debit.date.slice(0, 10)}
          </li>
        ))}
      </ul>

      {/* Form to input a new debit */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="description"
          placeholder="Description"
          required
        />
        <input
          type="number"
          name="amount"
          placeholder="Amount"
          step="0.01"
          required
        />
        <button type="submit">Add Debit</button>
      </form>

      {/* Link back to Home page */}
      <Link to="/">Return to Home</Link>
    </div>
  );
};

export default Debits;
