/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/

import React from 'react';
import { Link } from 'react-router-dom';

const Credits = (props) => {
  // This function is triggered when the form is submitted
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent the page from reloading on form submission

    // Get the values entered by the user in the form fields
    const description = e.target.description.value;
    const amount = parseFloat(e.target.amount.value); // Convert the input to a floating-point number
    const date = new Date().toISOString(); // Get the current date in ISO format (includes time)

    // Call the addCredit function passed as a prop from App.js to update the state
    props.addCredit({ description, amount, date });

    // Clear the form fields after submission
    e.target.reset();
  };

  return (
    <div>
      <h1>Credits</h1>

      {/* Display the list of credit transactions */}
      <ul>
        {props.credits.map((credit, index) => (
          <li key={index}>
            {/* Display description, formatted amount, and formatted date (only YYYY-MM-DD) */}
            {credit.description} - ${credit.amount.toFixed(2)} on {credit.date.slice(0, 10)}
          </li>
        ))}
      </ul>

      {/* Form to add a new credit */}
      <form onSubmit={handleSubmit}>
        <input type="text" name="description" placeholder="Description" required />
        <input type="number" name="amount" placeholder="Amount" step="0.01" required />
        <button type="submit">Add Credit</button>
      </form>

      {/* Navigation link to go back to the Home page */}
      <Link to="/">Return to Home</Link>
    </div>
  );
};

export default Credits;
